package assignment;

public class SwapTwoNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		int b=30;
		System.out.println("Before swap:"+a+" "+b);
		int temp=a;
	    a=b;
	    b=temp;
	    System.out.println("After swap:"+a+" "+b);
	}

}
